function randomIntFromInterval(e,a){return Math.floor(Math.random()*(a-e+1)+e)}function randPokemon(){return randomIntFromInterval(0,718).toString()}function generateName(e,a){var t="http://pokeapi.co/api/v1/"+e+randPokemon();$.ajax({type:"GET",url:t,dataType:"jsonp",async:!0,success:function(e){$(a).text(e.name)}})}function generateTypes(e,a){var t="http://pokeapi.co/api/v1/"+e+randPokemon();$.ajax({type:"GET",url:t,dataType:"jsonp",async:!0,success:function(e){for(var t="",n=0;n<e.types.length;n++){var o=e.types[n].name;o=o.charAt(0).toUpperCase()+o.slice(1,o.length),t+=o+" "}$(a).text(t)}})}function generateAbilities(e,a){var t="http://pokeapi.co/api/v1/"+e+randPokemon();$.ajax({type:"GET",url:t,dataType:"jsonp",async:!0,success:function(e){for(var t="",n=0;n<e.abilities.length;n++){var o=e.abilities[n].name;o=o.charAt(0).toUpperCase()+o.slice(1,o.length),t+="<li>"+o+"</li>"}$(a).html(t)}})}function generateSprite(e,a){var t="http://pokeapi.co/api/v1/"+e+randPokemon();$.ajax({type:"GET",url:t,dataType:"jsonp",async:!0,success:function(e){var t="http://pokeapi.co"+e.image;$(a).attr("src",t)}})}// Use all generate functions together to make a new random pokemon!
function makeAPokemon(){generateName("pokemon/","#name"),generateTypes("pokemon/","#types"),generateAbilities("pokemon/","#abilities"),generateSprite("sprite/","#sprite")}$("#generate").on("click",makeAPokemon),makeAPokemon();